const slimbot2 = require('slimbot2');
const slimbot2 = new slimbot2('614519785:bcf6be1ed458c9552b2104a5f94ebed52c433f15');

// Register listeners
slimbot2.on('message', message => {
  // reply when user sends a message
  slimbot2.sendMessage(message.chat.id, 'Message received');
});

slimbot2.on('edited_message', edited_message => {
  // reply when user edits a message
  slimbot2.sendMessage(edited_message.chat.id, 'Message edited');
});

// Call API
slimbot2.startPolling();

console.log('polling...');

setTimeout(() => {
  slimbot2.stopPolling();
}, 10000);
